import React, { useState } from "react";
const Counter = () => {
  //0에서 출발하고 1씩 증가하고 1씩 감소하는 count 상태
  const [count, setCount] = useState(0);

  const onIncrease = () => {
    //상태 변화 함수에 인자로 새로운 상태를 전달하면 됨
    setCount(count + 1);
  };

  const onDecrease = () => {
    //상태 변화 함수에 인자로 새로운 상태를 전달하면 됨
    setCount(count - 1);
  };

  return (
    <div>
      <h2>{count}</h2>
      <button onClick={onIncrease}>+</button>
      <button onClick={onDecrease}>-</button>
    </div>
  );
};

export default Counter;
