import React, { useEffect, useState } from "react";

const LifeCycle = () => {
  const [count, setCount] = useState(0);
  const [text, setText] = useState("");

  useEffect(() => {
    console.log("Mount!");
  }, []); //빈배열을 주면 mount된 시점에만 움직임, mount 시점에 무언가 하고 싶으면 빈배열을 넘겨주고 콜백함수에 하고 싶은 일을 넣어주면 됨
  useEffect(() => {
    console.log("Update!");
  });

  return (
    <div style={{ padding: 20 }}>
      <div>
        {count}
        <button
          onClick={() => {
            setCount(count + 1);
          }}
        >
          +
        </button>
        <div>
          <input
            value={text}
            onChange={(e) => {
              setText(e.target.value);
            }}
          ></input>
        </div>
      </div>
    </div>
  );
};

export default LifeCycle;
